import requestsimport hashlibimport sys# get password to testif len(sys.argv) <= 1:    print("\nUsage: password_hack_check password_to_check\n")else:    test_pwd = sys.argv[1]        # generate sha1    sha1pwd = hashlib.sha1(test_pwd.encode('utf-8')).hexdigest().upper()        # extract first 5 characters for the API    first_5 = sha1pwd[:5]    tail_after_5 = sha1pwd[5:]        # print(sha1pwd)    # print(first_5)    # print(tail_after_5)        # API call    url = 'https://api.pwnedpasswords.com/range/' + first_5    res = requests.get(url)        if res.status_code != 200:        raise RuntimeError(res.status_code)        # organise the response into hash and count    hashes = (line.split(':') for line in res.text.splitlines())    count = '0'        # check for matching hash and display the count    for h, c in hashes:        if h == tail_after_5:            count = c            break        print(f'\nThis password has been hacked ' + str(count) + ' times.\n')